targets.expr:
sort(
 sum by (release) (
  sum by (release) (
  kube_pod_container_status_running{release=~"$deployment", container!="airflow-cleanup-pods"}
 ) - 
 count by (release) (
  kube_pod_container_status_running{release=~"$deployment", container!="airflow-cleanup-pods"}
  )
 )
)

sort_desc(max by (container, pod, reason) (
 max by (container, pod) (
 sort_desc(kube_pod_container_status_waiting{release=~"$deployment", container!="airflow-cleanup-pods"})
 ) or
 max by (container, reason) (
 sort_desc(kube_pod_container_status_waiting_reason{release=~"$deployment"}==1)
 ) or
 max by (container, reason) (
 sort_desc(kube_pod_container_status_terminated_reason{release=~"$deployment", container!="airflow-cleanup-pods"}==1)
 )
))
