targets.expr:
sum by (release) (
  kube_pod_container_status_running{namespace=~".*-.*-.*-[0-9]{4}", release=~"$deployment"}
) -
count by (release) (
  kube_pod_container_status_running{namespace=~".*-.*-.*-[0-9]{4}", release=~"$deployment"}
)

sort_desc(max by (container, pod, reason) (
  max by (container, pod) (
    sort_desc(kube_pod_container_status_waiting{namespace=~".*-.*-.*-[0-9]{4}", release=~"$deployment"})
  ) or
  max by (container, reason) (
    sort_desc(kube_pod_container_status_waiting_reason{namespace=~".*-.*-.*-[0-9]{4}", release=~"$deployment"}==1)
  ) or
  max by (container, reason) (
    sort_desc(kube_pod_container_status_terminated_reason{namespace=~".*-.*-.*-[0-9]{4}", release=~"$deployment"}==1)
  )
))
